  /// Get response based on query keywords and language
  static String getResponse(String query, String language) {
    final lowerQuery = query.toLowerCase();
    String category = 'general';
    
    // Detect category from keywords
    if (lowerQuery.contains('sav') || lowerQuery.contains('рдмрдЪрдд') || lowerQuery.contains('рдмрдЪрд╛рдирд╛')) {
      category = 'savings';
    } else if (lowerQuery.contains('budget') || lowerQuery.contains('рдмрдЬрдЯ') || lowerQuery.contains('рдЦрд░реНрдЪ')) {
      category = 'budget';
    } else if (lowerQuery.contains('scheme') || lowerQuery.contains('рдпреЛрдЬрдирд╛') || lowerQuery.contains('рд╕рд░рдХрд╛рд░')) {
      category = 'schemes';
    } else if (lowerQuery.contains('business') || lowerQuery.contains('рд╡реНрдпрд╛рдкрд╛рд░') || lowerQuery.contains('рд╡реНрдпрд╡рд╕рд╛рдп')) {
      category = 'business';
    }
    
    // Get responses for category and language
    final categoryResponses = responses[category] ?? responses['general']!;
    final langResponses = categoryResponses[language] ?? categoryResponses['en']!;
    
    // Return random response from category
    return langResponses[DateTime.now().millisecond % langResponses.length];
  }

